function initMap(){map=new google.maps.Map(document.getElementById("map"),{center:{lat:37.414653,lng:-122.078533},zoom:15}),infoWindow=new google.maps.InfoWindow,ko.applyBindings(new LocationViewModel)}function mapError(){}function resizeMap(n){bounds=new google.maps.LatLngBounds;for(var o=0;o<n.length;o++)bounds.extend(n[o].getPosition())}var map,infoWindow,bounds;window.onresize=function(){map.fitBounds(bounds)};